        <script>
        var count = 10;

        // Function to decrement the timer and submit the form when the timer reaches zero
        function decrement() {
            count--;

            if (count === 0) {
                document.getElementById("quizForm").submit();
            } else {
                document.getElementById("count").textContent = count;
                setTimeout(decrement, 1000);
            }
        }

        // Start the countdown when the page loads
        decrement();


       
    </script>
    <script>
        score=0;
     function verifierAnswer(){
        var radioBtn = document.getElementsByClassName("answer1");
            if(radioBtn.value === <?php echo $answers['good_answer'] ?>){
             
                score+=1;
                document.getElementsById("score").textContent=score;
            } 
        }
        verifierAnswer();
